export class TextEnhancerError extends Error {
  constructor(message) {
    super(message)
    this.name = "TextEnhancerError"
  }
}

export class ValidationError extends TextEnhancerError {
  constructor(message) {
    super(message)
    this.name = "ValidationError"
  }
}

export class MissingFontSetError extends TextEnhancerError {
  constructor(setName) {
    super(`Font set "${setName}" is not defined in config.`)
    this.name = "MissingFontSetError"
    this.setName = setName
  }
}

export class MissingColorThemeError extends TextEnhancerError {
  constructor(themeName) {
    super(`Color theme "${themeName}" is not defined in config.`)
    this.name = "MissingColorThemeError"
    this.themeName = themeName
  }
}